// SpotifyProvider.tsx
import React, { FC, useState, useMemo } from "react";
import SpotifyContext from "./SpotifyContext";

interface Props {
  children: React.ReactNode;
}

const SpotifyProvider: FC<Props> = ({ children }) => {
  const [currentPlaylistID, setCurrentPlaylistID] = useState<string | null>(
    null
  );
  const [currentPlaylist, setCurrentPlaylist] =
    useState<SpotifyApi.SinglePlaylistResponse | null>(null);
  const [searchedPlaylist, setSearchedPlaylist] = useState<string>("");
  const [userPlaylists, setUserPlaylists] = useState<
    SpotifyApi.PlaylistObjectSimplified[]
  >([]);
  const [currentPlaylistTracks, setCurrentPlaylistTracks] = useState<
    SpotifyApi.PlaylistTrackObject[]
  >([]);
  const [flows, setFlows] = useState<Record<string, any>>({});

  const DEFAULT_IMG: string =
    "https://via.placeholder.com/1200x1200/424242/FFFFFF/?text=";

  const value = useMemo(
    () => ({
      currentPlaylistID,
      setCurrentPlaylistID,
      searchedPlaylist,
      setSearchedPlaylist,
      userPlaylists,
      setUserPlaylists,
      currentPlaylist,
      setCurrentPlaylist,
      currentPlaylistTracks,
      setCurrentPlaylistTracks,
      DEFAULT_IMG,
      flows,
setFlows
    }),
    [
      currentPlaylistID,
      searchedPlaylist,
      userPlaylists,
      currentPlaylist,
      currentPlaylistTracks,
    ]
  );

  return (
    <SpotifyContext.Provider value={value}>{children}</SpotifyContext.Provider>
  );
};

export default SpotifyProvider;
