import { FC, useState, useEffect } from "react";
import { Card } from "react-bootstrap";
import "./TrackCard.css";

const DEFAULT_IMG: string =
  "https://via.placeholder.com/1200x1200/424242/FFFFFF/?text=";

interface Props {
  track: SpotifyApi.PlaylistTrackObject;
}

const TrackCard: FC<Props> = ({ track }) => {
  const [isActive, setIsActive] = useState(false);

  return (
    <Card
      className={`text-left flex-row justify-content-between align-items-center playlist-card ${
        isActive ? "active" : "non-active"
      }`}
    >
      <Card.Img variant="left" src={track.track?.album.images[0].url} />
      <Card.Body className="d-flex justify-content-between">
        <Card.Title>{track.track?.name}</Card.Title>
        <Card.Text>
          {track.track?.artists
            .map((artist) => {
              return artist.name;
            })
            .join(", ")}{" "}
        </Card.Text>
      </Card.Body>
    </Card>
  );
};

export default TrackCard;
