import { FC, useState, useEffect } from "react";
import { Card } from "react-bootstrap";

interface Props {
  playlist: SpotifyApi.PlaylistObjectSimplified
  currentPlaylist: string | null;
  setCurrentPlaylist: (playlist: string) => void;
}

const PlaylistCard: FC<Props> = ({
  playlist,
  currentPlaylist,
  setCurrentPlaylist,
}) => {
  const [isActive, setIsActive] = useState(false);

  useEffect(() => {
    if (currentPlaylist === playlist.name) {
      setIsActive(true);
    } else {
      setIsActive(false);
    }
  }, [currentPlaylist, playlist.name]);

  const handleClick: () => void = () => {
    setCurrentPlaylist(playlist.name);
  };

  return (
    <Card
      className={`text-dark text-left ${isActive ? "active" : ""}`}
      onClick={handleClick}
      style={{
        textAlign: "left",
        backgroundColor: isActive ? "grey" : "white",
      }}
    >
      <Card.Body className="d-flex justify-content-between">
        <Card.Title>{playlist.name}</Card.Title>
        <Card.Text>{playlist.songs} Songs</Card.Text>
      </Card.Body>
    </Card>
  );
};

export default PlaylistCard;
