/* eslint-disable react/no-array-index-key */
import { FC, useState } from "react";
import { Container, Row, Col, Form } from "react-bootstrap";
import PlaylistCard from "./PlaylistCard";
import "./App.css";
import { useSpotify } from "./SpotifyContext";


const YourLibrary: FC<Props> = () => {
  const {
    currentPlaylistID,
    setCurrentPlaylistID,
    searchedPlaylist,
    setSearchedPlaylist,
    userPlaylists,
  } = useSpotify();

  return (
    <div className="mb-3">
      <div className="mb-3">
        <h2>Your Library</h2>
      </div>
      <Container className="d-flex flex-column py-3">
        <Form.Control
          type="search"
          placeholder="Search"
          value={searchedPlaylist}
          onChange={(e) => setSearchedPlaylist(e.target.value)}
        />
      </Container>
      <Container className="overflow-auto pannel-height">
        <Row xs={1} md={1} className="g-1">
          {userPlaylists == null ? (
            <h3>You have no playlists :&#40;</h3>
          ) : (
            userPlaylists.map((playlist, index) => (
              <Col key={index} className="d-flex flex-column">
                <PlaylistCard playlist={playlist} />
              </Col>
            ))
          )}
        </Row>
      </Container>
    </div>
  );
};

export default YourLibrary;
