import { FC, useState, useEffect } from "react";
import { Card } from "react-bootstrap";
import "./TrackCard.css";

const convertMsToMinSec = (dur: number | undefined) => {
  if (dur === undefined) {
    return "0:00";
  }
  let toret = "";
  toret += Math.floor(dur / 60000);
  toret += ":";
  const sec = Math.floor((dur % 60000) / 1000);
  if (sec < 10) {
    toret += "0";
  }
  toret += sec;
  return toret;
};

interface Props {
  track: SpotifyApi.PlaylistTrackObject;
}

const TrackCard: FC<Props> = ({ track }) => {
  const [isActive, setIsActive] = useState(false);

  return (
    <Card
      className={`text-left flex-row justify-content-start align-items-center playlist-card ${
        isActive ? "active" : "non-active"
      }`}
    >
      <Card.Img
        className="track-card-img"
        variant="left"
        src={track.track?.album.images[0].url}
      />
      <div className="d-flex flex-column justify-content-start ml-3">
        <Card.Title>{track.track?.name}</Card.Title>
        <Card.Text>
          {track.track?.artists
            .map((artist) => {
              return artist.name;
            })
            .join(", ")}
        </Card.Text>
      </div>
      <div>
        <Card.Text className="text-muted">{track.track?.album.name}</Card.Text>
        <Card.Text className="text-muted">
          {convertMsToMinSec(track.track?.duration_ms)}
        </Card.Text>
      </div>
    </Card>
  );
};

export default TrackCard;
