import { FC, useState, useEffect } from "react";
import { Card } from "react-bootstrap";
import "./PlaylistCard.css";

const DEFAULT_IMG: string =
  "https://via.placeholder.com/1200x1200/424242/FFFFFF/?text=";

interface Props {
  playlist: SpotifyApi.PlaylistObjectSimplified;
  currentPlaylistID: string | null;
  setcurrentPlaylistID: (playlist: string) => void;
}

const PlaylistCard: FC<Props> = ({
  playlist,
  currentPlaylistID,
  setcurrentPlaylistID,
}) => {
  const [isActive, setIsActive] = useState(false);

  useEffect(() => {
    if (currentPlaylistID === playlist.id) {
      setIsActive(true);
    } else {
      setIsActive(false);
    }
  }, [currentPlaylistID, playlist.name]);

  const handleClick: () => void = () => {
    setcurrentPlaylistID(playlist.id);
  };

  return (
    <Card
      className={`text-dark text-left flex-row justify-content-between align-items-center ${
        isActive ? "active" : "non-active"
      }`}
      onClick={handleClick}
    >
      {playlist.images.length === 0 && (
        <Card.Img variant="left" src={DEFAULT_IMG} className="playlist-image" />
      )}
      {playlist.images.length >= 1 && (
        <Card.Img
          variant="left"
          src={playlist.images[0].url}
          className="playlist-image"
        />
      )}
      <Card.Body className="d-flex justify-content-between">
        <Card.Title>{playlist.name}</Card.Title>
        <Card.Text>{playlist.tracks.total} Songs</Card.Text>
      </Card.Body>
    </Card>
  );
};

export default PlaylistCard;
